#!/usr/bin/env node

var connect = require('connect');
var httpd = require('./httpd');

var listenPort = 3010;
// The last number found will be interpreted as the port to listen on
process.argv.forEach(function(port){
    if(port.match(/^[0-9]+/)) {
        listenPort = parseInt(port, 10);
    }
});

console.log('Listening on port ' + listenPort);


var app = connect.createServer(httpd());
//app.listen(3000);

app.listen(listenPort);
